function generateCssRules(property, propertyName) {
    const styleElement = document.createElement("style");
    styleElement.classList.add("adex-css"); // Add a class to the style element
    document.head.appendChild(styleElement);

    const stylesheet = styleElement.sheet;

    for (let i = 0; i <= 1000; i++) {
        const rule = `/*
        * adlitoUI - JavaScript and CSS Utility for Adlito
        * Author: Jitesh chichole
        * Copyright (C) 2023 Your adlitoUI
        * License: MIT License
        */
        
        .${propertyName}${i} {
            ${property}: ${i}px;
        }`;

        stylesheet.insertRule(rule, stylesheet.cssRules.length);
    }
}

generateCssRules("width", "wd");
generateCssRules("height", "hg");
generateCssRules("top", "top");
