function ngenerateCssRules(property, propertyName) {
    const nstyleElement = document.createElement("style");
    nstyleElement.classList.add("nadex-css"); // Add a class to the style element
    document.head.appendChild(nstyleElement);

    const stylesheet = nstyleElement.sheet;

    for (let i = 1; i <= 1000; i++) {
        const rule = `/*
        * adlitoUI - JavaScript and CSS Utility for Adlito
        * Author: Jitesh chichole
        * Copyright (C) 2023 Your adlitoUI
        * License: MIT License
        */
        
        .n${propertyName}${i} {
            ${property}: -${i}px;
        }`;

        stylesheet.insertRule(rule, stylesheet.cssRules.length);
    }
}

ngenerateCssRules("width", "wd");
ngenerateCssRules("height", "hg");
ngenerateCssRules("top", "top");
ngenerateCssRules("bottom", "bottom");
ngenerateCssRules("left", "left");
ngenerateCssRules("right", "right");
ngenerateCssRules("font-size", "fs");
